import React from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: ""
    }
  }

  add = val => {
    this.setState({input: this.state.input + val});
  }

  equal = () => {
    this.setState({ input: math.eval(this.state.input) });
  }

  multiply = val => {
    if (val === "x") {
      this.setState({input: this.state.input + val.replace("x", "*")});
    }
}

  render() {
    return (
      <div className="app">
        <div className="calcCont">
          <Input input={this.state.input}></Input>
          <div className="row">
            <Button handleClick={this.add}>7</Button>
            <Button handleClick={this.add}>8</Button>
            <Button handleClick={this.add}>9</Button>
            <Button handleClick={this.handleMultiply}>x</Button>
          </div>
          <div className="row">
            <Button handleClick={this.add}>4</Button>
            <Button handleClick={this.add}>5</Button>
            <Button handleClick={this.add}>6</Button>
            <Button handleClick={this.add}>-</Button>
          </div>
          <div className="row">
            <Button handleClick={this.add}>1</Button>
            <Button handleClick={this.add}>2</Button>
            <Button handleClick={this.add}>3</Button>
            <Button handleClick={this.add}>+</Button>
          </div>
          <div className="row">
            <Button handleClick={this.add}>0</Button>
            <Button handleClick={this.add}>.</Button>
            <Button handleClick={() => this.equal()}>=</Button>
            <Button handleClick={this.add}>/</Button>
          </div>
          <div className="row">
            <ClearButton handleClear={() => this.setState({input: ""})}>clear</ClearButton>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
